INSERT INTO formengine_notice (id_notice,title,message,workgroup_key,date_debut,date_fin,id_diffusion,is_enabled,id_notification,notice_order,id_notice_group) VALUES 
  (1,'deces-lieuEtDate-remarqueLieu','\n                    Seuls les actes enregistrés à Paris peuvent être délivrés.\n                    Les actes de décès sont enregistrés à la mairie (d''arrondissement) du lieu du décès et, éventuellement\n		    transcrits, à la mairie (d''arrondissement) du dernier domicile du défunt. \n                    Pour plus d''informations, veuillez consulter \n                     \n                    <a target=\_blank\ href=\http://www.service-public.fr\>service-public.fr</a>\n                    .\n                ','all',NULL,NULL,-1,1,0,0,1);
INSERT INTO formengine_notice (id_notice,title,message,workgroup_key,date_debut,date_fin,id_diffusion,is_enabled,id_notification,notice_order,id_notice_group) VALUES 
  (2,'deces-lieuEtDate-remarqueDate','\n                \n                  <p>\n                  Les actes d''état civil détenus par les mairies d''arrondissement ne \n                  concernent que la période postérieure à 1903. Si le décès a eu lieu avant 1903, \n                  la demande doit être faite au : \n      			  </p>\n    			  <blockquote>\n    			        <p> \n    			        Service des Archives départementales<br />\n    			        18 boulevard Sérurier<br/>\n    			        75019 Paris <br/>\n    			        Tél : 01 53 72 41 23 \n    			        </p>\n    			        <p>\n    			        <a target=\parent\ href=\http://www.paris.fr/portail/Culture/Portal.lut?page_id=149\><span class=\aquo\>&raquo; </span>Lien vers les archives de Paris</a>\n    			        </p>\n    			      </blockquote>\n                \n                ','all',NULL,NULL,-1,1,0,0,1);
INSERT INTO formengine_notice (id_notice,title,message,workgroup_key,date_debut,date_fin,id_diffusion,is_enabled,id_notification,notice_order,id_notice_group) VALUES 
  (3,'deces-choixActe-remarquesTypesActes','\n             Une copie intégrale est une reproduction de l''acte original, \n             mentions marginales comprises.\n            ','all',NULL,NULL,-1,1,0,0,5);
