INSERT INTO formengine_notice (id_notice,title,message,workgroup_key,date_debut,date_fin,id_diffusion,is_enabled,id_notification,notice_order,id_notice_group) VALUES 
  (10,'mariage-lieuEtDate-remarqueLien','\n		Les notaires, les avocats, les représentants légaux et les administrations \n		spécifiquement autorisées qui peuvent avoir accès aux actes de l''état civil \n		de la personne qui leur a donné mandat sont invités à formuler leur demande \n		de copie intégrale ou d''extrait avec filiation de mariage \n		par courrier et non par Internet compte tenu de la nécessité de produire un \n		justificatif.\n            ','all',NULL,NULL,-1,1,0,0,2);
