INSERT INTO formengine_notice (id_notice,title,message,workgroup_key,date_debut,date_fin,id_diffusion,is_enabled,id_notification,notice_order,id_notice_group) VALUES 
  (15,'mariage-choixActe-informations','\n                \n               <p> Pour obtenir une copie intégrale ou un extrait avec filiation d''acte de mariage, le demandeur doit satisfaire 3 conditions (décret n° 97-852 du 16 septembre 1997) :\n                </p>\n                <ul>\n                <li>\n                Etre la personne que l''acte concerne, son conjoint, ses ascendants ou descendants, son représentant légal.\n                </li>\n                <li>\n                Etre majeur ou émancipé.\n                </li>\n                <li>\n                Etre en mesure d''indiquer, lors de la demande, les nom et prénom usuels des parents de la personne concernée par l''acte.\n                </li>\n                </ul>\n                <p>\n                Toute personne peut obtenir un extrait sans filiation d''acte de naissance.\n                </p>\n                \n            ','all',NULL,NULL,-1,1,0,0,8);
